import React from "react";
import { Card } from "react-bootstrap";

function Withdraw(){
  const [show, setShow] = React.useState(true);
  const [status, setStatus] = React.useState('');
  const [withdraw, setWithdraw] = React.useState(0);
  let balance = 100;

  function validate(num){
    if (isNaN(parseFloat(num))) {
      setStatus('Error: Please enter a number');
      setTimeout(() => setStatus(''),3000);
      return false;
    }
    return true;
}  

function handleSubmit() {
  console.log(withdraw);
  if (!validate(withdraw))   
   return;
  balance -= parseFloat(withdraw);
  // ctx.users[0].balance = balance;
  // event.preventDefault();
  alert(`Success! Withdrew ${withdraw} : New balance ${balance}`);
  setShow(false);
};

  function clearForm(){
    setWithdraw(0);
    setShow(true);
  }

  return (
    <Card style={{ backgroundColor: "yellow", color: "black", width: "18rem" }}>
        <Card.Header>Withdraw</Card.Header>
        <Card.Body>
            {show ? (  
                <>
                Your balance is currently: ${balance}<br/>
                <br/>
                Withdrawl Amount<br/>
                <input type="input" className="form-control" id="withdraw" placeholder="Withdrawl Amount" value={withdraw} onChange={e => setWithdraw(e.currentTarget.value)}/><br/>
                <button type="submit" className="btn btn-light" onClick={handleSubmit}>Withdraw</button>
                {status}
                </>
                ):(
                <>
                <h5>Success</h5>
                <button type="submit" className="btn btn-light" onClick={clearForm}>Make Another Withdrawl</button>
                </>
                )}

        </Card.Body>
    </Card>
  )
}

export default Withdraw;
