import React from "react";
import UserContext from "react";
import { Card } from "react-bootstrap";


class CreateAccount extends React.Component {
    constructor(props) {
        super(props);
    }

  render() {
    const [show, setShow]         = React.useState(true);
    const [status, setStatus]     = React.useState('');
    const [name, setName]         = React.useState('');
    const [email, setEmail]       = React.useState('');
    const [password, setPassword] = React.useState('');
    const ctx = React.useContext(UserContext);  

    function validate(field, label){
        if (!field) {
          setStatus('Error: ' + label);
          setTimeout(() => setStatus(''),3000);
          return false;
        }
        return true;
    }

    function handleCreate(){
      console.log(name,email,password);
      if (!validate(name,     'name'))     return;
      if (!validate(email,    'email'))    return;
      if (!validate(password, 'password')) return;
      ctx.users.push({name,email,password,balance:100});
      setShow(false);
    }    

    function clearForm(){
      setName('');
      setEmail('');
      setPassword('');
      setShow(true);
    }

    return (
        <Card style={{ backgroundColor: "primary", color: "black", width: "18rem" }}>
            <Card.Header>Create Account</Card.Header>
            <Card.Body>
                {show ? ( 
                    <>
                        Name<br/>
                        <input type="input" className="form-control" id="name" placeholder="Enter name" onChange={e => setName(e.currentTarget.value)} /><br/>
                        Email address<br/>
                        <input type="input" className="form-control" id="email" placeholder="Enter email" onChange={e => setEmail(e.currentTarget.value)}/><br/>
                        Password<br/>
                        <input type="password" className="form-control" id="password" placeholder="Enter password" onChange={e => setPassword(e.currentTarget.value)}/><br/>
                        <button type="submit" className="btn btn-light" onClick={handleCreate}>Create Account</button><br/>
                        {status}
                    </>
                    ):( 
                    <>
                        <h5>Success</h5>
                        <button type="submit" className="btn btn-light" onClick={clearForm}>Add another account</button> 
                    </> 
                    )}
            </Card.Body>
        </Card>
    );
    }
}

export default CreateAccount;
