import React from "react";
import UserContext from "react";
import { Card } from "react-bootstrap";

// const ctx = React.useContext(UserContext);  

class CreateAccount extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            show: true,
            status: this.status,
            name: this.name,
            email: this.email,
            password: this.password
        }
    }


    validate(field, label) {
      if (!field) {
        status = 'Error: ' + label;
        setTimeout(() => status = '', 3000);
        return false;
      }
      return true;
  }

   handleCreate() {
    console.log(name,email,password);
    if (!this.validate(this.name,     'name'))     return;
    if (!this.validate(this.email,    'email'))    return;
    if (!this.validate(this.password, 'password')) return;
    // ctx.users.push({name,email,password,balance:100});
    show = false;
  }    

   clearForm() {
    this.name = '';
    this.email = '';
    this.password = '';
    this.show = true;
  }

  render() {
    return (
        <Card style={{ backgroundColor: "primary", color: "black", width: "18rem" }}>
            <Card.Header>Create Account</Card.Header>
            <Card.Body>
                {show ? ( 
                    <div>
                        Name<br/>
                        <input type="input" className="form-control" id="name" placeholder="Enter name" value={name} onChange={e => this.name = e.currentTarget.value} /><br/>
                        Email address<br/>
                        <input type="input" className="form-control" id="email" placeholder="Enter email" value={email} onChange={e => this.email = e.currentTarget.value}/><br/>
                        Password<br/>
                        <input type="password" className="form-control" id="password" placeholder="Enter password" value={password} onChange={e => this.password = e.currentTarget.value}/><br/>
                        <button type="submit" className="btn btn-light" onClick={this.handleCreate}>Create Account</button><br/>
                        {status}
                    </div>
                    ):( 
                    <div>
                        <h5>Success</h5>
                        <button type="submit" className="btn btn-light" onClick={this.clearForm}>Add another account</button> 
                    </div> 
                    )}
            </Card.Body>
        </Card>
    );
    }
}

export default CreateAccount;
