import React from "react";
import { Card } from "react-bootstrap";


function Deposit(){
    const [show, setShow] = React.useState(true);
    const [status, setStatus] = React.useState('');
    const [deposit, setDeposit] = React.useState(0);
    let balance = 0;
    // let balance = parseFloat(ctx.users[0].balance);
    // console.log(balance);

  function validate(num){
      if (isNaN(parseFloat(num))) {
        setStatus('Error: Please enter a number');
        setTimeout(() => setStatus(''),3000);
        return false;
      }
      return true;
  }  

  function handleSubmit() {
    console.log(deposit);
    if (!validate(deposit))   
     return;
    balance += parseFloat(deposit);
    // ctx.users[0].balance = balance;
    // event.preventDefault();
    alert(`Deposited ${deposit} : New balance ${balance}`);
    setShow(false);
  };

  function clearForm(){
    setDeposit(0);
    setShow(true);
  }


  return (
    <Card style={{ backgroundColor: "green", color: "black", width: "18rem" }}>
        <Card.Header>Deposit</Card.Header>
        <Card.Body>
            {show ? (  
                <>
                Your balance is currently: {balance}<br/>
                Deposit Amount<br/>
                <input type="input" className="form-control" id="deposit" placeholder="Deposit Amount" value={deposit} onChange={e => setDeposit(e.currentTarget.value)}/><br/>
                <button type="submit" className="btn btn-light" onClick={handleSubmit}>Deposit</button><br/>
                {status}
                </>
                ):(
                <>
                <h5>Success</h5>
                <button type="submit" className="btn btn-light" onClick={clearForm}>Make Another Deposit</button>
                </> 
                )}
        </Card.Body> 
    </Card>
  )
}

export default Deposit;
