import React, { useContext } from "react";
import { Card } from "react-bootstrap";
import { UserContext } from "./context";

function CreateAccount() {
    const [show, setShow] = React.useState(true);
    const [status, setStatus] = React.useState('');
    const [name, setName] = React.useState('');
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');
    let users;
    console.log(JSON.stringify(users));
    // document.getElementById("nav1").style.color = "#FFF";
    // document.getElementById("nav1").style.backgroundColor = "#212529";

    function validate(field, label){
        if (!field) {
          setStatus(`Error: ${label} must be filled out`);
          setTimeout(() => setStatus(''),3000);
          return false;
        }
        return true;
    }

    function validatePassword(password) {
        if (password < 8) {
            setStatus(`Error: password must be at least 8 characters`);
            setTimeout(() => setStatus(''),3000);
            return false;
          }
          return true;           
    }

    function handleCreate(context){
      if (!validate(name,     'name'))     return;
      if (!validate(email,    'email'))    return;
      if (!validatePassword(password, 'password')) return;
      users.push({name, email, password, balance:100});
      context.setUsers(users);
      // console.log(JSON.stringify(users));
      setShow(false);
    }    

    function clearForm(){
      setName('');
      setEmail('');
      setPassword('');
      setShow(true);
    }

    return (
        <Card style={{ backgroundColor: "lightgray", color: "black", width: "18rem" }}>
            <Card.Header>Create Account</Card.Header>
            <Card.Body>
                {show ? ( 
                    <>
                        <UserContext.Consumer>
                            {(context) => (users = context.state.users)}
                        </UserContext.Consumer>
                        Name<br/>
                        <input type="input" className="form-control" id="name" placeholder="Enter name" onChange={e => setName(e.currentTarget.value)} /><br/>
                        Email address<br/>
                        <input type="input" className="form-control" id="email" placeholder="Enter email" onChange={e => setEmail(e.currentTarget.value)}/><br/>
                        Password<br/>
                        <input type="password" className="form-control" id="password" placeholder="Enter password" onChange={e => setPassword(e.currentTarget.value)}/><br/>
                        <UserContext.Consumer>
                            {(context) => (
                                <button type="submit" className="btn btn-light" onClick={handleCreate(context)}>Create Account</button>
                            )}
                        </UserContext.Consumer>
                        <br/>
                        {status}
                    </>
                    ):( 
                    <>
                        <h5>Success</h5>
                        <button type="submit" className="btn btn-light" onClick={clearForm}>Add another account</button> 
                    </> 
                    )}
            </Card.Body>
        </Card>
    );
    }

export default CreateAccount;
